import React from "react";
import styles from "./AreaWeekMonth.module.sass";
import {connect} from 'react-redux';



 function AreaWeekMonth({ isOpen, activeMonth}) {
  const chengeWeek = () => {
    if(this.state.activeMonth) {
      this.setState({          
        activeMonth: !this.state.activeMonth,
        isOpen: !isOpen,          
    })
    }else {
      this.setState({          
        isOpen: !this.state.isOpen
      })
    }      
  }
  const chengeMonth = () => {
    if(this.state.activeMonth) {
      this.setState({          
        isOpen: !this.state.isOpen,          
    })
    }else {
      this.setState({          
        activeMonth: !this.state.activeMonth,
        isOpen: !this.state.isOpen          
      })
    }      
  }
  return (
    <>
    {isOpen && (
      <div className={styles.container}>
        <button onClick={chengeWeek} className={styles.button}>
          This week
        </button>
        <button onClick={chengeMonth} className={styles.button}>
          This month
        </button>
      </div>
    )}
  </>
  )
}

const mapStateToProps = (store) => ({
  isOpen: store.reducer.isOpen, 
  activeMonth: store.reducer.activeMonth,
})

export default connect(mapStateToProps)(AreaWeekMonth);
