import React from "react";
import moment from "moment";
import PropTypes  from 'prop-types';
import styles from "./AreaOfMonth.module.sass";
import arrowUp from "../../../assets/img/chevron-up.png";
import arrowDown from "../../../assets/img/chevron-down.png";


function AreaOfMonth({renderWeekMonth, currentMonth, changePrevMonth, changeNextMonth, isOpen, activeMonth, currentStateMonth}) {
  const clonState = moment(currentStateMonth)
  const firstDayWeek = currentMonth.startOf('week').format("D");
  const lastDayWeek = currentMonth.endOf('week').format("D");
  const textPrew = 'Prew';
  const textNext = 'Next';
  const prewMonth = clonState.add(-1, "M").format("MMM");
  const curMonth = currentStateMonth.format(activeMonth ? "MMMM" : `MMM ${firstDayWeek} - ${lastDayWeek} `);
  const nextMonth = clonState.add(2, "M").format("MMM");
  const daysWeek = ["S", "M", "T", "W", "T", "F", "S"];

  // const renderWeekDays = (item, key) => {
  //   return <div key={key}>{item}</div>;
  // };

  return (
    <div className={styles.container}>
      <div className={styles.containerIn}>
        <p className={styles.textMonth} onClick={changePrevMonth}>
          {activeMonth ? prewMonth : textPrew}
        </p>
        <div className={styles.current}>
          <p onClick={renderWeekMonth} className={styles.active}>
            {curMonth}
          </p>
          <img
            onClick={renderWeekMonth}
            className={styles.arrow}
            src={isOpen ? arrowUp : arrowDown}
            alt="arrow"
          />
        </div>
          <p className={styles.textMonth} onClick={changeNextMonth}>
            {activeMonth ? nextMonth : textNext}
          </p>
      </div>
      <div>
        {!isOpen && (
          <div className={styles.daysWeek}>{daysWeek.map(renderWeekDays)}</div>
        )}
      </div>
    </div>
  );
}

AreaOfMonth.defaultProps = {
  renderWeekMonth: () => {},
  currentMonth: moment(),
  currentStateMonth: () => {},
  changePrevMonth: () => {},
  changeNextMonth: () => {},
  isOpen: false,
  activeMonth: true
};

AreaOfMonth.propTypes = {
  renderWeekMonth: PropTypes.func.isRequired,
  currentStateMonth: PropTypes.object.isRequired,
  changePrevMonth: PropTypes.func.isRequired,
  changeNextMonth: PropTypes.func.isRequired,
  isOpen: PropTypes.bool.isRequired,
  activeMonth: PropTypes.bool.isRequired
};


export default AreaOfMonth;
