import ACTION from '../action/actionTypes';

const initialState = {
    isOpen: false,
    activeMonth: true,
    currentDate: moment

};

export default function(state = initialState, action) {
    switch(action.type) {
        case ACTION.OPEN_TAB_WEEK_MONTH:
            return {
                ...state,
                isOpen: !state.isOpen
            }
        case ACTION.ACTIVE_MONTH_SWITCH:
            return {
                ...state,
                activeMonth: !state.activeMonth,
                isOpen: !state.isOpen

            }
       
        default: return state;
       
    }
}
