import ACTION from "../action/actionTypes";
import moment from "moment";

const initialState = {
  isOpen: false,
  activeMonth: true,
  currentMonth: moment(),
  isActiveDay: moment()
};
export default function(state = initialState, action) {
  console.log("reducer " + state.currentMonth.format("MMMM"))
  console.log(...state)


  switch (action.type) {
    case ACTION.OPEN_TAB_WEEK_MONTH:
      return {
        ...state,
        isOpen: !state.isOpen
      };
    case ACTION.ACTIVE_MONTH_SWITCH:
      return {
        ...state,
        activeMonth: !state.activeMonth,
        isOpen: !state.isOpen
      };
    case ACTION.CHANGE_NEXT_MONTH:
      return {
        ...state,
        currentMonth: state.currentMonth
      };
    case ACTION.CHANGE_PREW_MONTH:
        return {
            ...state,
            currentMonth: state.currentMonth


        }
    default:
      return state;
      
  }
  
}
